const result = $flow.output;

const sourceDocuments = result[0].sourceDocuments || [];

/*
* Format into:
abc
sources: {source: 'a', page: 12}
*/
const formattedSources = sourceDocuments.map(item => {
        const pageContent = item.pageContent;
        const metadata = `Sources: ${JSON.stringify(item.metadata)}`;
        return `${pageContent}\n${metadata}`;
    }).join('\n\n');

return {
  sources: formattedSources
};

